const Test = () => {
  const [hover, setHover] = useState(false);

  const HoverData = "jel radi ili jel ne radi joj boze radi ili ne radi";

  const onHover = (e) => {
    e.preventDefault();
    setHover(true);
    console.log("hovered");
  };

  const onHoverOver = (e) => {
    e.preventDefault();
    setHover(false);
  };

  return (
    <div>
      {/* if hover is true then only show the text */}
      {hover && <div style={{fontSize: 50}}>
        jebo majku
        <p style={{fontSize: 30}}>
          test</p>
      </div>}
      <img
      //div is not centerd thats why im using marginLeft
        style={styles.album_2}
       /* style={{

          width: '25%',
        }
        }*/
        onMouseEnter={(e) => onHover(e)}
        onMouseLeave={(e) => onHoverOver(e)}
        src={require("./empty_vinyl.png")}
      />
      {/*<p>Hover the image</p>*/}
    </div>
  );
};

export default function App() {
  const fadeAnim = useRef(new Animated.Value(0)).current;

  const fadeIn = () => {
    // Will change fadeAnim value to 1 in 5 seconds
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 5000
    }).start();
  };

  const fadeOut = () => {
    // Will change fadeAnim value to 0 in 3 seconds
    Animated.timing(fadeAnim, {
      toValue: 0,
      duration: 3000
    }).start();
  };

  return (
    <SafeAreaView style={styles.container}>
      <Animated.View
        style={[
          styles.fadingContainer,
          {
            // Bind opacity to animated value
            opacity: fadeAnim
          }
        ]}
      >
        <Text style={styles.fadingText}>Fading View!</Text>
      </Animated.View>
      <View style={styles.buttonRow}>
        <Button title="Fade In View" onPress={fadeIn} />
        <Button title="Fade Out View" onPress={fadeOut} />
      </View>
    </SafeAreaView>
    /*
    <SafeAreaView style={styles.container}>
     { <Text style={styles.Header}>How bad is your music taste?</Text>}
     <AnimatedTyping text={["How bad is your music taste?", "12345678"]} />
