import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, SafeAreaView, Button,Alert, Image, Animated } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import React, {useRef, useState} from 'react';
//import { styles } from './styleApp.js';
import AnimatedTyping from './AnimatedTyping.js';
import FadeInOut from 'react-native-fade-in-out';

export default function App() {
  const [visible, setVisible] = useState(true);

  const toggleVisible = () => {
    setVisible(!visible);
  }

  const [isShown, setIsShown] = useState(false);

  const fadeIn = () => {
    // Will change fadeAnim value to 1 in 5 seconds
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 500
    }).start();
  };

  const fadeOut = () => {
    // Will change fadeAnim value to 0 in 3 seconds
    Animated.timing(fadeAnim, {
      toValue: 0,
      duration: 500
    }).start();
  };

  return (
    <SafeAreaView style={styles.container}>
      <div className="App">
      <button
        onMouseEnter={() => {toggleVisible;toggleVisible}}
        onMouseLeave={() => setIsShown(false)}>
        Hover over me!
      </button>
      <FadeInOut visible={visible} duration={1000}>
        
      {isShown && (
        <div>
          I'll appear when you hover over the button.
        </div>
      )}
      </FadeInOut>
      

    </div>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center"
  },
  fadingContainer: {
    padding: 20,
    backgroundColor: "powderblue"
  },
  fadingText: {
    fontSize: 28
  },
  buttonRow: {
    flexBasis: 100,
    justifyContent: "space-evenly",
    marginVertical: 16
  }
});
