import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { styles } from './styleApp.js';
import {View, Text, StyleSheet, TouchableOpacity, SafeAreaView, Button, Pressable } from 'react-native';
import AnimatedTyping from './AnimatedTyping.js';
import { spotifyCredentials } from './secrets';
import SpotifyWebAPI from 'spotify-web-api-js';
import * as WebBrowser from 'expo-web-browser';
//import * as AuthSession from 'expo-auth-session';
import { makeRedirectUri, useAuthRequest } from 'expo-auth-session';
import { encode as btoa } from 'base-64';
import axios from 'axios';
//the scope for our analytics
const scopesArr = ['user-modify-playback-state'];
const scopes = scopesArr.join(' ');
//Navigation import
import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import ScreenOne from './ScreenOne.js';
import ScreenTwo from './ScreenTwo.js';


const ScreenThree = props => {
    const [ShowAnswer1, setShowAnswer1] = useState(false);//false 
    const [ShowReply1, setShowReply1] = useState(false)
    const [ShowQuestion2, setShowQuestion2] = useState(false); 
    const [ShowAnswer2, setShowAnswer2] = useState(false);
    const [ShowQuestion3, setShowQuestion3] = useState(false); 
    const [ShowAnswer3, setShowAnswer3] = useState(false);
    const [ShowQuestion4, setShowQuestion4] = useState(false); 
    const [ShowAnswer4, setShowAnswer4] = useState(false);
  
    const back = () => {
      props.navigation.navigate('Music Judger');
    };

    return (
        <View style={styles.screen3}>
  
        <AnimatedTyping text={["Okay so lets start", "How old are you?"]} onComplete={() => setShowAnswer1(true)}/>

        {ShowAnswer1 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {setShowReply1(true)}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >
            ) : null}

        {ShowAnswer1 ? (
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {setShowReply1(true)}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}

        {ShowReply1 ? (
            <AnimatedTyping text={["Insult 1"]} onComplete={() => setShowQuestion2(true) }/>
        ) : null}

        {ShowQuestion2 ? (
            <AnimatedTyping text={["Do your friends know you listen to this song: filler ?"]} onComplete={() => setShowAnswer2(true) }/>
        ) : null}

        {ShowAnswer2 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {setShowReply2(true)}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >
        ) : null}

        {ShowAnswer2 ? (
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {setShowReply2(true)}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}

        {ShowReply2 ? (
            <AnimatedTyping text={["Insult 2"]} onComplete={() => setShowQuestion3(true) }/>
        ) : null}
  
        {ShowQuestion3 ? (
            <AnimatedTyping text={["If you had to kill one of these artists who would it be?"]} onComplete={() => setShowAnswer3(true) }/>
        ) : null}

        {ShowAnswer3 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {setShowReply3(true)}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >
        ) : null}

        {ShowAnswer3 ? (
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {setShowReply3(true)}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}

        {ShowReply3 ? (
            <AnimatedTyping text={["Insult 3"]} onComplete={() => setShowQuestion4(true) }/>
        ) : null}
          
        {ShowQuestion4 ? (
            <AnimatedTyping text={["If you had to kill one of these artists who would it be?"]} onComplete={() => setShowAnswer4(true) }/>
        ) : null}

        {ShowAnswer4 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {setShowReply4(true)}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >
        ) : null}

        {ShowAnswer4 ? (
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {setShowReply4(true)}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}

        {ShowReply4 ? (
            <AnimatedTyping text={["Insult 4"]} onComplete={() => setShowButton(true) }/>
        ) : null}

        {setShowButton ? (
        <Button 
            style={styles.buttomStyle}
            title="back"
            color={"#000000"}
            onPress={back}>
          </Button>
          ) : null} 
      </View>
    );
  };

  export default ScreenThree;
