import React, { useState } from "react";
import { StatusBar } from "expo-status-bar";
import { styles } from "./styleApp.js";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  SafeAreaView,
  Button,
  Pressable,
  Image,
  Platform,
} from "react-native";
import AnimatedTyping from "./AnimatedTyping.js";
import { spotifyCredentials } from "./secrets";
import SpotifyWebAPI from "spotify-web-api-js";
import * as WebBrowser from "expo-web-browser";
//import * as AuthSession from 'expo-auth-session';
import { makeRedirectUri, useAuthRequest } from "expo-auth-session";
import { encode as btoa } from "base-64";
import axios from "axios";
//the scope for our analytics
const scopesArr = ["user-modify-playback-state"];
const scopes = scopesArr.join(" ");
//Navigation import
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

import { ProgressChart } from "react-native-chart-kit";

const ScreenOne = (props) => {
  //onPress To Navigate
  const onPress = () => {
    props.navigation.navigate("ScreenTwo");
  };

  // each value represents a goal ring in Progress chart
  const data = {
    labels: ["Swim", "Bike", "Run"], // optional
    data: [0.4, 0.6, 0.5],
  };

  const chartConfig = {
    backgroundGradientFrom: "white",
    backgroundGradientTo: "white",
    color: (opacity = 1) => `rgba(10, 10, 10, ${opacity})`,
    strokeWidth: 2, // optional, default 3
    barPercentage: 0.5,
    useShadowColorFromDataset: false, // optional
  };

  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.Header}>How bad is your music taste?</Text>

      <ProgressChart
        data={data}
        width={400}
        height={220}
        strokeWidth={16}
        radius={32}
        chartConfig={chartConfig}
        hideLegend={false}
        style={{
          fontSize: 1,
          fontFamily: "monospace",
        }}
      />

      <img style={styles.album_2} src={require("./empty_vinyl.png")} />

      <Button
        color={"#000000"}
        title="Find Out!"
        //color={"#000000"}
        onPress={onPress}
      ></Button>

      <Text style={styles.bottomTextLeft}>
        Project by Timotej, Viktor and Merisa
      </Text>

      <StatusBar style="auto" />
    </SafeAreaView>
  );
};
export default ScreenOne;
