import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, SafeAreaView, Button,Alert, Image, Animated } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import React, {useRef, useState} from 'react';
//import { styles } from './styleApp.js';
import AnimatedTyping from './AnimatedTyping.js';
import FadeInOut from 'react-native-fade-in-out';

export default function App() {
  const [isShown, setIsShown] = useState(false);
  const [visible, setVisible] = useState(true);

  const toggleVisible = () => {
    setVisible(!visible);
  }
  return (
    <div className="App">
      
      <button
        onMouseEnter={() => {
          setVisible(!visible);
          setIsShown(true)}}
        onMouseLeave={() => setIsShown(false)}>
        Hover over me!
      </button>
      {isShown && (
        
        <FadeInOut visible={!visible} duration={1000}>
        <Text>Duration = 100</Text>
        </FadeInOut>
      )}
    </div>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center"
  },
  fadingContainer: {
    padding: 20,
    backgroundColor: "powderblue"
  },
  fadingText: {
    fontSize: 28
  },
  buttonRow: {
    flexBasis: 100,
    justifyContent: "space-evenly",
    marginVertical: 16
  }
});
