import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { styles } from './styleApp.js';
import {View, Text, StyleSheet, TouchableOpacity, SafeAreaView, Button, Pressable } from 'react-native';
import AnimatedTyping from './AnimatedTyping.js';
import { spotifyCredentials } from './secrets';
import SpotifyWebAPI from 'spotify-web-api-js';
import * as WebBrowser from 'expo-web-browser';
//import * as AuthSession from 'expo-auth-session';
import { makeRedirectUri, useAuthRequest } from 'expo-auth-session';
import { encode as btoa } from 'base-64';
import axios from 'axios';
//the scope for our analytics
const scopesArr = ['user-modify-playback-state'];
const scopes = scopesArr.join(' ');
//Navigation import
import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import ScreenOne from './ScreenOne.js';
import ScreenTwo from './ScreenTwo.js';


const ScreenThree = props => {
    const [ShowAnswer1, setShowAnswer1] = useState(false);//false 
    const [ShowQuestion2, setShowQuestion2] = useState(false);//false 
    const [ShowAnswer2, setShowAnswer2] = useState(false);
  
    const back = () => {
      props.navigation.navigate('Music Judger');
    };

    return (
        <View style={styles.screen3}>
  
        <AnimatedTyping text={["First text", "question 1:"]} onComplete={() => setShowAnswer1(true)}/>

        {ShowAnswer1 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {setShowQuestion2(true)}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >
            ) : null}

        {ShowAnswer1 ? (
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {setShowQuestion2(true)}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}

        {ShowQuestion2 ? (
            <AnimatedTyping text={["question 2:"]} onComplete={() => setShowAnswer2(true) }/>
        ) : null}

        {ShowAnswer2 ? (
              <Pressable  
              style={styles.button2Style}
              
              onPress={()=> {}}><Text style={{color: "white"}}>answer 1</Text>
            </Pressable >,
            <Pressable  
            style={styles.button2Style}
            
            onPress={()=> {}}><Text style={{color: "white"}}>answer 2</Text>
            </Pressable >
          ) : null}
  
        <Button 
            style={styles.buttomStyle}
            title="back"
            color={"#000000"}
            onPress={back}>
          </Button>
      </View>
    );
  };

  export default ScreenThree;
