      />*/}

      <Button 
        style={styles.buttomStyle}
        title="Find Out!"
        color={"#000000"}
        onPress={() => alert('uf jebote')}>
      </Button>

      <Text style={styles.bottomTextLeft}>Project by Timotej, Viktor and Merisa</Text>


      <StatusBar style="auto" /> 
    </SafeAreaView>
  );
}

