const Test = () => {
  const [hover, setHover] = useState(false);

  const HoverData = "Click or pinch to Zoom Image";

  const onHover = (e) => {
    e.preventDefault();
    setHover(true);
    console.log("hovered");
  };

  const onHoverOver = (e) => {
    e.preventDefault();
    setHover(false);
  };
  return (
    <div className="flex_center py-5">
      {/* if hover is true then only show the text */}
      {hover && <p className={hover}>{HoverData}</p>}
      <img
        onMouseEnter={(e) => onHover(e)}
        onMouseLeave={(e) => onHoverOver(e)}
        alt=""
        className="img-responsive"
      />
      <p>Hover the image</p>
    </div>
  );
};

export default function App() {
  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.Header}>How bad is your music taste?</Text>
      <Test />
      <Image
          style={styles.album}
	  	  //file budemo vec poslozili, test path
          source={require('./empty_vinyl.png')}


      />

      <Button 
        style={styles.buttomStyle}
        title="Find Out!"
        color={"#000000"}
        onPress={() => alert('uf jebote')}>
      </Button>

      <Text style={styles.bottomTextLeft}>Project by Timotej, Viktor and Merisa</Text>


      <StatusBar style="auto" /> 
    </SafeAreaView>
  );
}

